import {creareSelector, createSelector} from 'reselect';

const selectCart=state=>state.cart;
export const selectCartItems=createSelector(
    [selectCart],
    cart=>cart.cartItems
);

export const selectCartItemsCount= createSelector(
    [selectCartItems],
    cartItems=>
    cartItems
    .reduce((accumilatedQuantity,cartItem)=>
    accumilatedQuantity+cartItem.quantity,0)
);
