import React, { useContext, useState } from 'react';
import { ReactComponent as Logo } from '../../assets/1531643.svg';
import { auth } from '../../firebase/firebase.utils';
import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdown from '../cart-drop-down/cart-drop-down.component';
import './header.styles.scss';
import {
	OuterContainer,
	OptionsContainer,
	OptionDiv,
	OptionLink,
	LogoContainer,
} from './header.style';
import CartContext from '../../contexts/cart-context/cart.context';
import CurrentUserContext from '../../contexts/current-user/current-user.context';
const Header = () => {
	const currentUser = useContext(CurrentUserContext);
	const [hidden, setHidden] = useState(false);
	const toggleHidden = () => setHidden(!hidden);
	return (
		<OuterContainer>
			<LogoContainer to='/'>
				<Logo className='logo' />
			</LogoContainer>
			<OptionsContainer>
				<OptionLink to='/shop'>SHOP</OptionLink>
				<OptionLink to='/shop'>CONTACT</OptionLink>
				{currentUser ? (
					<OptionDiv onClick={() => auth.signOut()}>
						SIGN OUT
					</OptionDiv>
				) : (
					<OptionLink to='/signin'>SIGN IN</OptionLink>
				)}
				<CartContext.Provider value={{ hidden, toggleHidden }}>
					<CartIcon />
				</CartContext.Provider>
			</OptionsContainer>
			{hidden ? <CartDropdown /> : ''}
		</OuterContainer>
	);
};

export default Header; //now we recive the value of current user directlly from the user
//  reducer to this component no need or state drilling so new we can remove passing the state (current user) to this comopnent from the parent
