import React from 'react';
import FormInput from '../form/form.component'
import './sign-in.styles.scss'

class SignIn extends React.Component{
    constructor(){
        super();
        this.state={
                email:'',
                password:''
        }
    }
    handleChange=e=>{
        const {value,name}=e.target;
        this.setState({[name]:value})
    }
    handleSubmit=(e)=>{
        e.preventDefault();
        this.setState({email:'', password:''})
    }

    render(){
        return(
            <div className='sign-in'>
            <h2>I alredy have an account</h2>
            <span>sign-in with your email and password</span>
            <FormInput className='form' onSubmit={this.handleSubmit}>
                <input handleChange={this.handleChange} name='email' type="email" vlaue={this.state.email}required/>
                <label > Email</label>
                <input handleChange={this.handleChange} name='password' type="password" required/>
                <label > PassWord</label>
                <input type="submit" value='sign-in'/>
             </FormInput>
            </div>
        )
    }
}
export default SignIn;
