import styled ,{css}from 'styled-components';


export const Group=styled.div`
  position: relative;
margin: 45px 0;
`
const shrinkLabel=css`
    top: -14px;
    font-size: 12px;
    color: $main-color;
  `
const shouldShrink=({ex})=>{
    if(!ex)
    {
        return shrinkLabel;
    }
}

export const FormInput=styled.input`
    background: none;
    background-color: none;
    color: gray;
    font-size: 18px;
    padding: 10px 10px 10px 5px;
    display: block;
    width: 100%;
    border: none;
    border-radius: 0;
    border-bottom: 1px solid black;
    margin: 25px 0;

    &:focus {
      outline: none;
    }

`


export const FormInputLable=styled.label`
color: pink;
font-size: 16px;
font-weight: normal;
position: absolute;
pointer-events: none;
left: 5px;
top: 10px;
transition: 300ms ease all;


`
